mapper.select:
SELECT VOLUNTEERBOARDNO, VOLUNTEERBOARDTITLE, VOLUNTEERBOARDCONTENT, VOLUNTEERDOCMAJOR, VOLUNTEERS, VOLUNTEERBOARDIMAGE, VOLUNTEERAREA, VOLUNTEERLOCATION, VOLUNTEERPERIOD, VOLUNTEERRECRUITMENTPERIOD, RECRUITMENTSTATUS, accrue FROM ( SELECT /*+ INDEX_DESC(TABLE_VOLUNTEER_BOARD PK_VOLUNTEER_BOARD) */ ROWNUM R, VOLUNTEERBOARDNO, VOLUNTEERBOARDTITLE, VOLUNTEERBOARDCONTENT, VOLUNTEERDOCMAJOR, VOLUNTEERS, VOLUNTEERBOARDIMAGE, VOLUNTEERAREA, VOLUNTEERLOCATION, VOLUNTEERPERIOD, VOLUNTEERRECRUITMENTPERIOD, RECRUITMENTSTATUS, accrue FROM ( SELECT b.*, (SELECT NVL(sum(applicants),0) FROM TABLE_APPLICANTS WHERE volunteerBoardNo=b.VOLUNTEERBOARDNO) accrue FROM TABLE_VOLUNTEER_BOARD b)
WHERE
ROWNUM <= #{pageNum} * #{amount} ORDER BY VOLUNTEERBOARDNO DESC ) WHERE R > (#{pageNum} - 1) * #{amount}

mapper.update:
UPDATE TABLE_VOLUNTEER_BOARD b SET volunteerApplicants =
(SELECT NVL(sum(applicants),0) FROM TABLE_APPLICANTS  WHERE volunteerBoardNo=b.VOLUNTEERBOARDNO)
WHERE volunteerBoardNo=#{volunteerBoardNo}
